import{_ as t,V as d,W as a,$ as r}from"./framework-e28fa486.js";const e={},h=r('<h1 id="艾因家的甜品" tabindex="-1"><a class="header-anchor" href="#艾因家的甜品" aria-hidden="true">#</a> 艾因家的甜品</h1><h2 id="椰子冻" tabindex="-1"><a class="header-anchor" href="#椰子冻" aria-hidden="true">#</a> 椰子冻</h2><h3 id="白凉粉版本" tabindex="-1"><a class="header-anchor" href="#白凉粉版本" aria-hidden="true">#</a> 白凉粉版本</h3><h4 id="制作材料" tabindex="-1"><a class="header-anchor" href="#制作材料" aria-hidden="true">#</a> 制作材料</h4><table><thead><tr><th>材料</th><th>数量</th></tr></thead><tbody><tr><td>白凉粉</td><td>250g</td></tr><tr><td>牛奶</td><td>1000ml</td></tr><tr><td>椰乳</td><td>300ml</td></tr><tr><td>椰子水</td><td>500ml</td></tr><tr><td>总计</td><td>1800ml</td></tr></tbody></table><h4 id="制作步骤" tabindex="-1"><a class="header-anchor" href="#制作步骤" aria-hidden="true">#</a> 制作步骤</h4><ol><li>加热除白凉粉以外的材料，到沸腾状态，将白凉粉倒入然后搅拌均匀</li><li>降温至常温，然后冰箱冷藏3-6小时</li></ol><h4 id="特点" tabindex="-1"><a class="header-anchor" href="#特点" aria-hidden="true">#</a> 特点</h4><p>白凉粉自带甜度，椰子水、椰乳也自带甜度，甜度调整主要靠牛奶进行调整。</p><ul><li>优点：顺滑度较高</li><li>缺点：凝固度白凉粉不如吉利丁，甜度难以把控。</li></ul><h3 id="吉利丁版本" tabindex="-1"><a class="header-anchor" href="#吉利丁版本" aria-hidden="true">#</a> 吉利丁版本</h3><h4 id="制作材料-1" tabindex="-1"><a class="header-anchor" href="#制作材料-1" aria-hidden="true">#</a> 制作材料</h4><table><thead><tr><th>材料</th><th>数量</th></tr></thead><tbody><tr><td>吉利丁</td><td>6片-30g</td></tr><tr><td>牛奶</td><td>700ml</td></tr><tr><td>椰子水</td><td>500ml</td></tr><tr><td>椰奶</td><td>400ml</td></tr><tr><td>椰乳</td><td>400ml</td></tr><tr><td>总计</td><td>2000ml</td></tr></tbody></table><h4 id="制作步骤-1" tabindex="-1"><a class="header-anchor" href="#制作步骤-1" aria-hidden="true">#</a> 制作步骤</h4><ol><li>吉利丁15度水泡软</li><li>加热除吉利丁以外的材料，到70度，将吉利丁混入然后搅拌均匀</li><li>降温至常温，然后冰箱冷藏3-6小时</li></ol><h4 id="特点-1" tabindex="-1"><a class="header-anchor" href="#特点-1" aria-hidden="true">#</a> 特点</h4><p>甜度可以通过牛奶和椰奶调节，可以根据自己喜好调节吉利丁数量，可以增加一倍吉利丁制作椰子糕。</p><h2 id="生巧" tabindex="-1"><a class="header-anchor" href="#生巧" aria-hidden="true">#</a> 生巧</h2><h3 id="配方一-经过验证的" tabindex="-1"><a class="header-anchor" href="#配方一-经过验证的" aria-hidden="true">#</a> 配方一 经过验证的</h3><table><thead><tr><th>材料</th><th>用料/g</th></tr></thead><tbody><tr><td>嘉利宝黑巧</td><td>500</td></tr><tr><td>淡奶油</td><td>400</td></tr><tr><td>无盐黄油</td><td>100</td></tr><tr><td>麦芽糖浆</td><td>100</td></tr><tr><td>朗姆酒</td><td>50</td></tr></tbody></table><h3 id="配方二-b站获取的" tabindex="-1"><a class="header-anchor" href="#配方二-b站获取的" aria-hidden="true">#</a> 配方二 B站获取的</h3><table><thead><tr><th>名称</th><th>用料/g</th></tr></thead><tbody><tr><td>牛奶巧克力</td><td>450</td></tr><tr><td>黑巧</td><td>225</td></tr><tr><td>淡奶油</td><td>374</td></tr><tr><td>无盐黄油</td><td>36</td></tr><tr><td>葡萄糖浆</td><td>25</td></tr></tbody></table><h3 id="制作步骤-2" tabindex="-1"><a class="header-anchor" href="#制作步骤-2" aria-hidden="true">#</a> 制作步骤</h3><ol><li>巧克力和黄油称重隔热水化开，热水温度不超过70度</li><li>淡奶油和糖浆混合小火烧开，倒入融化后的巧克力黄油，倒入后让它们先沟通一会，三分钟后再去搅匀</li><li>加入朗姆酒，搅拌均匀，可以通过搅拌器提高顺滑度</li><li>准备好模具，铺上锡纸，将搅拌好的巧克力酱倒入模具，用刮刀抹平</li><li>冷藏2-3小时后，切成大小均匀的块，撒上可可粉</li></ol>',24),i=[h];function l(n,c){return d(),a("div",null,i)}const s=t(e,[["render",l],["__file","艾因家的甜品.html.vue"]]);export{s as default};
